import './styles.css'
import { useState } from 'react'
import { BlocoRepositorio } from '../../components/BlocoRepositorio'

const Home = () => {
    const [repo, setRepo] = useState()
    const [autor, setAutor] = useState()
    const [resultados, setResultados] = useState([])

    const handleClick = async () => {
        if(resultados.map(element => { if(element.autor === autor && element.repo === repo) { return false } return true})) {
            const dataRequest = await fetch(`https://api.github.com/repos/${autor}/${repo}`)
            const resultado = await dataRequest.json()

            const {description} = resultado
            setResultados(prev => [...prev, {autor, repo, description}])
        }

        
    }

    console.log(resultados)

    return (
        <>
            <div className="container-pesquisa">
                <input type="text" placeholder="Autor" className="input" onChange={(event) => {
                    setAutor(event.target.value)
                }}/>
                <input type="text" placeholder="RepositÃ³rio" className="input" onChange={(event) => {
                    setRepo(event.target.value)
                }}/>
                <button className='botao' onClick={handleClick}>Adicionar</button>
            </div>

            <BlocoRepositorio nome="Titulo" descricao="adasdasd adsa"/>
        </>
    )
}

export { Home }
